// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

contract PythStructs {
    // A price with a degree of uncertainty, represented as a price +- a confidence interval.
    //
    // The confidence interval roughly corresponds to the standard error of a normal distribution.
    // Both the price and confidence are stored in a fixed-point numeric representation,
    // `x * (10^expo)`, where `expo` is the exponent.
    //
    // Please refer to the documentation at https://docs.pyth.network/documentation/pythnet-price-feeds/best-practices for how
    // to how this price safely.
    struct Price {
        // Price
        int64 price;
        // Confidence interval around the price
        uint64 conf;
        // Price exponent
        int32 expo;
        // Unix timestamp describing when the price was published
        uint publishTime;
    }

    // PriceFeed represents a current aggregate price from pyth publisher feeds.
    struct PriceFeed {
        // The price ID.
        bytes32 id;
        // Latest available price
        Price price;
        // Latest available exponentially-weighted moving average price
        Price emaPrice;
    }

    struct TwapPriceFeed {
        // The price ID.
        bytes32 id;
        // Start time of the TWAP
        uint64 startTime;
        // End time of the TWAP
        uint64 endTime;
        // TWAP price
        Price twap;
        // Down slot ratio represents the ratio of price feed updates that were missed or unavailable
        // during the TWAP period, expressed as a fixed-point number between 0 and 1e6 (100%).
        // For example:
        //   - 0 means all price updates were available
        //   - 500_000 means 50% of updates were missed
        //   - 1_000_000 means all updates were missed
        // This can be used to assess the quality/reliability of the TWAP calculation.
        // Applications should define a maximum acceptable ratio (e.g. 100000 for 10%)
        // and revert if downSlotsRatio exceeds it.
        uint32 downSlotsRatio;
    }

    // Information used to calculate time-weighted average prices (TWAP)
    struct TwapPriceInfo {
        // slot 1
        int128 cumulativePrice;
        uint128 cumulativeConf;
        // slot 2
        uint64 numDownSlots;
        uint64 publishSlot;
        uint64 publishTime;
        uint64 prevPublishTime;
        // slot 3
        int32 expo;
    }
}

contracts/MetricChallengeDAO.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MetricChallengeDAO
 * @dev A decentralized prediction market for metrics with staking mechanisms
 * @notice Users can submit metrics, stake on outcomes, and earn rewards based on accuracy and timing
 */
contract MetricChallengeDAO is ReentrancyGuard, Ownable {

    // --- Data Structures ---

    struct Stake {
        address staker;
        uint256 timestamp;
    }

    enum MetricStatus {
        Pending,
        Resolved
    }

    struct Metric {
        uint256 id;
        uint256 expectedLossPercent; // Stored as basis points (e.g., 1000 = 10%)
        uint256 duration;
        uint256 startTime;
        MetricStatus status;
        uint256 bountyCollateral;
        Stake[] stakesInFavor;
        Stake[] stakesAgainst;
    }

    // --- State Variables ---

    mapping(uint256 => Metric) public metrics;
    uint256 public nextMetricID;
    uint256 public fixedStakeAmount; // Fixed stake amount in wei

    // Contract balance tracking
    uint256 public totalContractBalance;

    // --- Events ---

    event MetricSubmitted(uint256 indexed metricID, address indexed submitter);
    event Staked(uint256 indexed metricID, address indexed staker, string side);
    event MetricResolved(uint256 indexed metricID, string winningSide);
    event RewardDistributed(address indexed staker, uint256 amount);
    event StakeAmountUpdated(uint256 newAmount);

    // --- Errors ---

    error MetricNotFound();
    error StakingPeriodOver();
    error MetricAlreadyResolved();
    error InsufficientPayment();
    error TransferFailed();
    error InvalidParameters();

    // --- Constructor ---

    constructor(uint256 _fixedStakeAmount) Ownable(msg.sender) {
        nextMetricID = 1;
        fixedStakeAmount = _fixedStakeAmount;
    }

    // --- Modifiers ---

    modifier metricExists(uint256 metricID) {
        if (metricID == 0 || metricID >= nextMetricID) {
            revert MetricNotFound();
        }
        _;
    }

    modifier stakingPeriodActive(uint256 metricID) {
        Metric storage metric = metrics[metricID];
        if (block.timestamp >= metric.startTime + metric.duration) {
            revert StakingPeriodOver();
        }
        _;
    }

    modifier onlyPending(uint256 metricID) {
        if (metrics[metricID].status != MetricStatus.Pending) {
            revert MetricAlreadyResolved();
        }
        _;
    }

    // --- Main Functions ---

    /**
     * @dev Submit a new metric for prediction
     * @param expectedLossPercent Expected loss percentage in basis points (e.g., 1000 = 10%)
     * @param duration Duration of staking period in seconds
     * @return metricID The ID of the newly created metric
     */
    function submitMetric(
        uint256 expectedLossPercent,
        uint256 duration,
        uint256 bountyCollateral
    ) external payable nonReentrant returns (uint256) {
        if (expectedLossPercent > 10000 || duration == 0) {
            revert InvalidParameters();
        }

        uint256 metricID = nextMetricID;

        // Create new metric
        Metric storage newMetric = metrics[metricID];
        newMetric.id = metricID;
        newMetric.expectedLossPercent = expectedLossPercent;
        newMetric.duration = duration;
        newMetric.startTime = block.timestamp;
        newMetric.status = MetricStatus.Pending;
        newMetric.bountyCollateral = bountyCollateral;

        // Update contract state
        nextMetricID++;
        totalContractBalance += bountyCollateral;

        emit MetricSubmitted(metricID, msg.sender);
        return metricID;
    }

    /**
     * @dev Stake in favor of a metric's prediction
     * @param metricID The ID of the metric to stake on
     */
    function stakeInFavor(uint256 metricID) 
        external 
        nonReentrant 
        metricExists(metricID) 
        stakingPeriodActive(metricID) 
        onlyPending(metricID) 
    {

        Metric storage metric = metrics[metricID];
        metric.stakesInFavor.push(Stake({
            staker: msg.sender,
            timestamp: block.timestamp
        }));
        emit Staked(metricID, msg.sender, "InFavor");
    }

    /**
     * @dev Stake against a metric's prediction
     * @param metricID The ID of the metric to stake on
     */
    function stakeAgainst(uint256 metricID) 
        external 
        nonReentrant 
        metricExists(metricID) 
        stakingPeriodActive(metricID) 
        onlyPending(metricID) 
    {

        Metric storage metric = metrics[metricID];
        metric.stakesAgainst.push(Stake({
            staker: msg.sender,
            timestamp: block.timestamp
        }));        
        emit Staked(metricID, msg.sender, "Against");
    }

    /**
     * @dev Resolve a metric and distribute rewards
     * @param metricID The ID of the metric to resolve
     * @param actualLossPercent The actual loss percentage in basis points
     */
    function resolveMetric(uint256 metricID, uint256 actualLossPercent) 
        external 
        onlyOwner 
        nonReentrant 
        metricExists(metricID) 
        onlyPending(metricID) 
    {
        if (actualLossPercent > 10000) {
            revert InvalidParameters();
        }

        Metric storage metric = metrics[metricID];

        // Determine winning side
        bool inFavorWins = actualLossPercent >= metric.expectedLossPercent;
        Stake[] storage winningStakes = inFavorWins ? metric.stakesInFavor : metric.stakesAgainst;
        Stake[] storage losingStakes = inFavorWins ? metric.stakesAgainst : metric.stakesInFavor;
        string memory winningSide = inFavorWins ? "InFavor" : "Against";

        // Calculate reward pool
        uint256 rewardPool = metric.bountyCollateral + (losingStakes.length * fixedStakeAmount);

        if (winningStakes.length > 0 && rewardPool > 0) {
            // Calculate total earlyness for proportional distribution
            uint256 totalEarlyness = 0;
            for (uint256 i = 0; i < winningStakes.length; i++) {
                uint256 stakingTime = winningStakes[i].timestamp - metric.startTime;
                uint256 earlyness = metric.duration - stakingTime;
                totalEarlyness += earlyness;
            }

            // Distribute rewards based on earlyness
            if (totalEarlyness > 0) {
                for (uint256 i = 0; i < winningStakes.length; i++) {
                    uint256 stakingTime = winningStakes[i].timestamp - metric.startTime;
                    uint256 earlyness = metric.duration - stakingTime;
                    uint256 reward = (earlyness * rewardPool) / totalEarlyness;

                    if (reward > 0) {
                        totalContractBalance -= reward;
                        (bool success, ) = payable(winningStakes[i].staker).call{value: reward}("");
                        if (!success) {
                            revert TransferFailed();
                        }
                        emit RewardDistributed(winningStakes[i].staker, reward);
                    }
                }
            }
        }

        // Mark metric as resolved
        metric.status = MetricStatus.Resolved;

        emit MetricResolved(metricID, winningSide);
    }

    // --- View Functions ---

    /**
     * @dev Get metric details
     * @param metricID The ID of the metric
     * @return id The metric ID
     * @return expectedLossPercent Expected loss percentage in basis points
     * @return duration Duration of staking period in seconds
     * @return startTime Start timestamp of the metric
     * @return status Current status of the metric
     * @return bountyCollateral Bounty collateral amount
     * @return stakesInFavorCount Number of stakes in favor
     * @return stakesAgainstCount Number of stakes against
     */
    function getMetric(uint256 metricID) 
        external 
        view 
        metricExists(metricID) 
        returns (
            uint256 id,
            uint256 expectedLossPercent,
            uint256 duration,
            uint256 startTime,
            MetricStatus status,
            uint256 bountyCollateral,
            uint256 stakesInFavorCount,
            uint256 stakesAgainstCount
        ) 
    {
        Metric storage metric = metrics[metricID];
        return (
            metric.id,
            metric.expectedLossPercent,
            metric.duration,
            metric.startTime,
            metric.status,
            metric.bountyCollateral,
            metric.stakesInFavor.length,
            metric.stakesAgainst.length
        );
    }

    /**
     * @dev Get stakes in favor of a metric
     * @param metricID The ID of the metric
     * @return Array of stakes in favor
     */
    function getStakesInFavor(uint256 metricID) 
        external 
        view 
        metricExists(metricID) 
        returns (Stake[] memory) 
    {
        return metrics[metricID].stakesInFavor;
    }

    /**
     * @dev Get stakes against a metric
     * @param metricID The ID of the metric
     * @return Array of stakes against
     */
    function getStakesAgainst(uint256 metricID) 
        external 
        view 
        metricExists(metricID) 
        returns (Stake[] memory) 
    {
        return metrics[metricID].stakesAgainst;
    }

    /**
     * @dev Check if staking period is active for a metric
     * @param metricID The ID of the metric
     * @return True if staking period is active
     */
    function isStakingActive(uint256 metricID) 
        external 
        view 
        metricExists(metricID) 
        returns (bool) 
    {
        Metric storage metric = metrics[metricID];
        return block.timestamp < metric.startTime + metric.duration && 
               metric.status == MetricStatus.Pending;
    }

    // --- Admin Functions ---

    /**
     * @dev Update the fixed stake amount (only owner)
     * @param newAmount New stake amount in wei
     */
    function updateFixedStakeAmount(uint256 newAmount) external onlyOwner {
        if (newAmount == 0) {
            revert InvalidParameters();
        }
        fixedStakeAmount = newAmount;
        emit StakeAmountUpdated(newAmount);
    }

    /**
     * @dev Emergency withdraw function (only owner)
     * @param amount Amount to withdraw in wei
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        if (amount > address(this).balance) {
            revert InsufficientPayment();
        }

        (bool success, ) = payable(owner()).call{value: amount}("");
        if (!success) {
            revert TransferFailed();
        }
    }

    /**
     * @dev Get contract balance
     * @return Contract balance in wei
     */
    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    // --- Fallback Functions ---

    receive() external payable {
        totalContractBalance += msg.value;
    }

    fallback() external payable {
        totalContractBalance += msg.value;
    }
}

contracts/SimpleShortContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@pythnetwork/pyth-sdk-solidity/IPyth.sol";
import "@pythnetwork/pyth-sdk-solidity/PythStructs.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SimpleShortContract
 * @dev A simple ETH shorting contract using Pyth Network price feeds
 * @notice Users can open short positions on ETH and profit from price decreases
 */
contract SimpleShortContract is ReentrancyGuard, Ownable {
    IPyth public immutable pyth;

    struct ShortPosition {
        uint256 collateralAmount;    // ETH collateral provided
        int64 entryPrice;           // Price when short opened (8 decimals)
        uint256 timestamp;          // When position opened
        bool active;               // Position status
    }

    mapping(address => ShortPosition) public positions;

    // Pyth Network configuration for Sepolia
    address constant PYTH_CONTRACT_SEPOLIA = 0xDd24f84D36bF92C65F92307595C6B99D36b6f8c4;

    // ETH/USD price feed ID from Pyth Network
    bytes32 constant ETH_USD_PRICE_ID = 0xff61491a931112ddf1bd8147cd1b641375f79f5825126d665480874634fd0ace;

    // Safety parameters
    uint256 constant PRICE_STALENESS_THRESHOLD = 300; // 5 minutes
    uint256 constant MAX_PROFIT_PERCENTAGE = 200; // 200% max profit
    uint256 constant MIN_COLLATERAL = 0.001 ether; // Minimum 0.001 ETH

    // Events
    event ShortOpened(address indexed user, uint256 collateral, int64 price, uint256 timestamp);
    event ShortClosed(address indexed user, int256 pnl, int64 exitPrice, uint256 timestamp);
    event EmergencyWithdraw(address indexed owner, uint256 amount);

    // Custom errors
    error InsufficientCollateral();
    error PositionAlreadyExists();
    error NoActivePosition();
    error InvalidPrice();
    error PriceTooStale();
    error TransferFailed();
    error InsufficientUpdateFee();

    constructor() Ownable(msg.sender) {
        pyth = IPyth(PYTH_CONTRACT_SEPOLIA);
    }

    /**
     * @dev Get current ETH price with validation
     * @return price Current ETH/USD price (8 decimals)
     * @return timestamp Price publish timestamp
     */
    function getCurrentPrice() public view returns (int64 price, uint64 timestamp) {
        PythStructs.Price memory priceData = pyth.getPriceUnsafe(ETH_USD_PRICE_ID);

        if (priceData.price <= 0) revert InvalidPrice();
        if (block.timestamp - priceData.publishTime > PRICE_STALENESS_THRESHOLD) {
            revert PriceTooStale();
        }

        return (priceData.price, uint64(priceData.publishTime));
    }

    /**
     * @dev Open a short position on ETH
     * @param priceUpdateData Pyth price update data
     */
    function openShort(bytes[] calldata priceUpdateData) external payable nonReentrant {
        if (msg.value < MIN_COLLATERAL) revert InsufficientCollateral();
        if (positions[msg.sender].active) revert PositionAlreadyExists();

        // Calculate update fee
        uint updateFee = pyth.getUpdateFee(priceUpdateData);
        if (msg.value <= updateFee) revert InsufficientUpdateFee();

        // Update Pyth price feeds
        pyth.updatePriceFeeds{value: updateFee}(priceUpdateData);

        // Get current ETH price with validation
        (int64 currentPrice, ) = getCurrentPrice();

        // Calculate actual collateral after update fee
        uint256 actualCollateral = msg.value - updateFee;

        // Store position
        positions[msg.sender] = ShortPosition({
            collateralAmount: actualCollateral,
            entryPrice: currentPrice,
            timestamp: block.timestamp,
            active: true
        });

        emit ShortOpened(msg.sender, actualCollateral, currentPrice, block.timestamp);
    }

    /**
     * @dev Close short position and calculate P&L
     * @param priceUpdateData Pyth price update data
     */
    function closeShort(bytes[] calldata priceUpdateData) external payable nonReentrant {
        ShortPosition storage position = positions[msg.sender];
        if (!position.active) revert NoActivePosition();

        // Calculate update fee
        uint updateFee = pyth.getUpdateFee(priceUpdateData);
        if (msg.value < updateFee) revert InsufficientUpdateFee();

        // Update Pyth price feeds
        pyth.updatePriceFeeds{value: updateFee}(priceUpdateData);

        // Get current ETH price with validation
        (int64 currentPrice, ) = getCurrentPrice();

        // Calculate P&L: profit if price went down, loss if price went up
        int256 priceDiff = position.entryPrice - currentPrice;
        int256 pnlPercentage = (priceDiff * 10000) / position.entryPrice; // Basis points
        int256 pnl = (int256(position.collateralAmount) * pnlPercentage) / 10000;

        // Calculate final payout with profit cap
        uint256 finalPayout = 0;
        if (pnl > 0) {
            // Profit: cap at MAX_PROFIT_PERCENTAGE
            uint256 profit = uint256(pnl);
            uint256 maxProfit = (position.collateralAmount * MAX_PROFIT_PERCENTAGE) / 100;
            if (profit > maxProfit) {
                profit = maxProfit;
            }
            finalPayout = position.collateralAmount + profit;
        } else {
            // Loss: subtract from collateral
            uint256 loss = uint256(-pnl);
            if (loss >= position.collateralAmount) {
                finalPayout = 0; // Total loss
            } else {
                finalPayout = position.collateralAmount - loss;
            }
        }

        // Mark position as closed
        position.active = false;

        // Send payout (subtract update fee if any remaining)
        if (finalPayout > 0) {
            (bool success, ) = payable(msg.sender).call{value: finalPayout}("");
            if (!success) revert TransferFailed();
        }

        // Refund excess ETH sent for update fee
        if (msg.value > updateFee) {
            (bool success, ) = payable(msg.sender).call{value: msg.value - updateFee}("");
            if (!success) revert TransferFailed();
        }

        emit ShortClosed(msg.sender, pnl, currentPrice, block.timestamp);
    }

    /**
     * @dev View current P&L without closing position
     * @param user Address of the user
     * @return pnl Current profit/loss in wei
     * @return currentPrice Current ETH price
     * @return pnlPercentage P&L as percentage (basis points)
     */
    function viewPnL(address user) external view returns (int256 pnl, int64 currentPrice, int256 pnlPercentage) {
        ShortPosition storage position = positions[user];
        if (!position.active) revert NoActivePosition();

        // Get current price (may revert if stale)
        (currentPrice, ) = getCurrentPrice();

        // Calculate P&L
        int256 priceDiff = position.entryPrice - currentPrice;
        pnlPercentage = (priceDiff * 10000) / position.entryPrice; // Basis points
        pnl = (int256(position.collateralAmount) * pnlPercentage) / 10000;

        return (pnl, currentPrice, pnlPercentage);
    }

    /**
     * @dev Get position details for a user
     * @param user Address of the user
     * @return position The user's position details
     */
    function getPosition(address user) external view returns (ShortPosition memory position) {
        return positions[user];
    }

    /**
     * @dev Check if user has an active position
     * @param user Address of the user
     * @return hasPosition True if user has active position
     */
    function hasActivePosition(address user) external view returns (bool hasPosition) {
        return positions[user].active;
    }

    /**
     * @dev Get the required update fee for price updates
     * @param priceUpdateData Pyth price update data
     * @return fee Required fee in wei
     */
    function getUpdateFee(bytes[] calldata priceUpdateData) external view returns (uint fee) {
        return pyth.getUpdateFee(priceUpdateData);
    }

    /**
     * @dev Get contract statistics
     * @return totalBalance Total ETH balance in contract
     * @return pythContract Address of Pyth contract
     * @return priceId ETH/USD price feed ID
     */
    function getContractInfo() external view returns (
        uint256 totalBalance,
        address pythContract,
        bytes32 priceId
    ) {
        return (
            address(this).balance,
            address(pyth),
            ETH_USD_PRICE_ID
        );
    }

    /**
     * @dev Emergency withdraw function (owner only)
     * @param amount Amount to withdraw (0 for all)
     */
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        uint256 withdrawAmount = amount == 0 ? address(this).balance : amount;
        require(withdrawAmount <= address(this).balance, "Insufficient balance");

        (bool success, ) = payable(owner()).call{value: withdrawAmount}("");
        if (!success) revert TransferFailed();

        emit EmergencyWithdraw(owner(), withdrawAmount);
    }

    /**
     * @dev Receive function to accept ETH
     */
    receive() external payable {
        // Allow contract to receive ETH for price updates and collateral
    }

    /**
     * @dev Fallback function
     */
    fallback() external payable {
        revert("Function not found");
    }
}

